private String mode; public String getMode() { Object responseObject = FacesContext.getCurrentInstance().getExternalContext().getRequest(); if (responseObject instanceof RenderRequest) {
RenderRequest renderRequest = (RenderRequest)responseObject; if(renderRequest.getPortletMode().toString().equals(mode)){ mode = null; }else{ mode = renderRequest.getPortletMode().toString(); }
} return mode; } public String getFromView() { PortletSession portletSession = (PortletSession)FacesContext.getCurrentInstance().getExternalContext().getSession(false); String viewId = (String)portletSession.getAttribute(&quot;javax.portlet.faces.viewIdHistory.view&quot;);
viewId = viewId.substring(0,viewId.indexOf(&rdquo;?&ldquo;)); return viewId; } public void setMode(String mode) {
this.mode = mode; }
